TinyMCESettings settings =
    new TinyMCESettings(TinyMCESettings.Theme.advanced);

// few TinyMce editor settings
settings.setToolbarLocation(TinyMCESettings.Location.top);
settings.setStatusbarLocation(TinyMCESettings.Location.bottom);

// add default buttons
settings.add(TinyMCESettings.newdocument,
    TinyMCESettings.Toolbar.first, TinyMCESettings.Position.before);
settings.add(TinyMCESettings.separator,
    TinyMCESettings.Toolbar.first, TinyMCESettings.Position.before);

// add plugin's button
SavePlugin savePlugin = new SavePlugin();
settings.add(savePlugin.getSaveButton(),
    TinyMCESettings.Toolbar.first, TinyMCESettings.Position.before);

add(new TinyMCEPanel("tinyMCE", settings));
